import React, { useState, useEffect } from "react";
import contohta from "../../assets/image/aksara1/contohta.png";
import contohca from "../../assets/image/aksara1/contohca.png";
import contohga from "../../assets/image/aksara1/contohga.png";
const NggolekiTembang = () => {
  const grid = [
    ["T", "A", "R", "I"],
    ["M", "A", "C", "A"],
    ["G", "A", "W", "A"],
  ];

  const words = ["TARI", "MACA", "GAWA"];
  const clues = {
    TARI: contohta,
    MACA: contohca,
    GAWA: contohga,
  };

  const [selectedWord, setSelectedWord] = useState(null);
  const [foundWords, setFoundWords] = useState([]);

  const checkWord = (selectedWord) => {
    let wordExists = false;

    // Cek horizontal
    for (let i = 0; i < grid.length; i++) {
      if (grid[i].join("") === selectedWord) {
        wordExists = true;
      }
    }

    // Cek vertikal
    for (let i = 0; i < grid[0].length; i++) {
      let verticalWord = "";
      for (let j = 0; j < grid.length; j++) {
        verticalWord += grid[j][i];
      }
      if (verticalWord === selectedWord) {
        wordExists = true;
      }
    }

    if (wordExists) {
      setFoundWords([...foundWords, selectedWord]);
    }
  };

  useEffect(() => {
    if (selectedWord) {
      checkWord(selectedWord);
    }
  }, [selectedWord]);

  return (
    <div className="word-search-game">
      <div className="word-search-grid">
        {grid.map((row, rowIndex) => (
          <div key={rowIndex} className="word-search-row">
            {row.map((letter, colIndex) => (
              <div key={colIndex} className="word-search-cell">
                {letter}
              </div>
            ))}
          </div>
        ))}
      </div>

      <div className="word-search-clues">
        {words.map((word) => (
          <img
            key={word}
            src={clues[word]}
            alt={word}
            onClick={() => setSelectedWord(word)}
            className={`word-search-clue-image ${
              selectedWord === word ? "selected" : ""
            }`}
          />
        ))}
      </div>

      <div className="word-search-found-words">
        <h3>Words Found:</h3>
        <ul>
          {foundWords.map((word, index) => (
            <li key={index}>{word}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default NggolekiTembang;
