import React, { useState } from "react";
import BelajarImage from "../assets/image/Belajar.png";

const IsiBelajar1 = () => {
  const cards = [
    {
      title: "Ha",
      description: "This is the description for card 1.",
      image: BelajarImage,
      buttonText: "Pelafalan",
    },
    {
      title: "Card 2",
      description: "This is the description for card 2.",
      image: "https://via.placeholder.com/150",
      buttonText: "Learn More",
    },
    {
      title: "Card 3",
      description: "This is the description for card 3.",
      image: "https://via.placeholder.com/150",
      buttonText: "Learn More",
    },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  const nextCard = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % cards.length);
  };

  const prevCard = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? cards.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="mode">
      <h1>Belajar Aksara Nglegena</h1>
      <div className="carousel-container">
        <button onClick={prevCard}>‹</button>
        <div className="card">
          <h2>{cards[currentIndex].title}</h2>
          <p>{cards[currentIndex].description}</p>
          <img src={cards[currentIndex].image} alt="Card" />
          <button>{cards[currentIndex].buttonText}</button>
        </div>
        <button onClick={nextCard}>›</button>
      </div>
    </div>
  );
};

export default IsiBelajar1;
