const data = {
  across: {
    1: {
      clue: `img:${dramaria}`,
      answer: "DRAMARIA",
      row: 1,
      col: 2,
    },
    3: {
      clue: `img:${opera}`,
      answer: "OPERA",
      row: 4,
      col: 0,
    },
    5: {
      clue: `img:${teater}`,
      answer: "TEATER",
      row: 6,
      col: 0,
    },
  },
  down: {
    2: {
      clue: `img:${tragedi}`,
      answer: "TRAGEDI",
      row: 0,
      col: 7,
    },
    4: {
      clue: `img:${dagelan}`,
      answer: "DAGELAN",
      row: 1,
      col: 2,
    },
  },
};

export default function UtakAtikGathukMurda() {
  const [isCorrect, setIsCorrect] = useState(false);
  const navigate = useNavigate();

  const handleCrosswordCorrect = () => {
    setIsCorrect(true);
  };

  const handleNextPage = () => {
    navigate("/aksaramurda/kuis3/nyocokakegambarmurda");
  };

  return (
    <div className="contain">
      <h1 className="p-20 m-5 text-center">Utak Atik Gathuk Murda</h1>
      <div className="game-container">
        <div className="crossword">
          <Crossword
            data={data}
            onCrosswordCorrect={handleCrosswordCorrect}
            useCustomClueRender={true} // Assuming this allows for custom rendering
          />
        </div>
      </div>
      {isCorrect && (
        <div className="text-center">
          <button onClick={handleNextPage} className="btn btn-primary mt-4">
            Next Page
          </button>
        </div>
      )}
    </div>
  );
}
