import React, { useState } from "react";
import dramaria from "../../assets/image/kuis/dramaria.png";
import tragedi from "../../assets/image/kuis/tragedi.png";
import dagelan from "../../assets/image/kuis/dagelan.png";
import opera from "../../assets/image/kuis/opera.png";
import teater from "../../assets/image/kuis/teater.png";

const crosswordData = {
  grid: Array(8).fill(Array(8).fill("")), // Empty grid
  clues: {
    across: [
      {
        clue: <img src={dramaria} alt="Drama Clue" />,
        answer: "DRAMARIA",
        row: 1,
        col: 2,
        direction: "across",
      },
      {
        clue: <img src={opera} alt="Opera Clue" />,
        answer: "OPERA",
        row: 4,
        col: 0,
        direction: "across",
      },
      {
        clue: <img src={teater} alt="Teater Clue" />,
        answer: "TEATER",
        row: 6,
        col: 0,
        direction: "across",
      },
    ],
    down: [
      {
        clue: <img src={tragedi} alt="Tragedi Clue" />,
        answer: "TRAGEDI",
        row: 0,
        col: 7,
        direction: "down",
      },
      {
        clue: <img src={dagelan} alt="Dagelan Clue" />,
        answer: "DAGELAN",
        row: 1,
        col: 2,
        direction: "down",
      },
    ],
  },
};

const Crossword = ({ onCrosswordCorrect }) => {
  const [highlightedCells, setHighlightedCells] = useState([]);

  const highlightCells = (row, col, direction, length) => {
    const cells = [];
    for (let i = 0; i < length; i++) {
      if (direction === "across") {
        cells.push({ row, col: col + i });
      } else {
        cells.push({ row: row + i, col });
      }
    }
    setHighlightedCells(cells);
  };

  const getCellClass = (row, col) => {
    return highlightedCells.some((c) => c.row === row && c.col === col)
      ? "highlighted"
      : "";
  };

  return (
    <div className="crossword-grid">
      {crosswordData.grid.map((row, rowIndex) => (
        <div key={rowIndex} className="crossword-row">
          {row.map((cell, colIndex) => (
            <input
              key={colIndex}
              value={cell}
              onChange={(e) => handleChange(rowIndex, colIndex, e.target.value)}
              maxLength={1}
              className={`crossword-cell ${getCellClass(rowIndex, colIndex)}`}
            />
          ))}
        </div>
      ))}
      <div className="clues">
        <h2>Across</h2>
        {crosswordData.clues.across.map((clue, index) => (
          <div
            key={index}
            className="clue"
            onClick={() =>
              highlightCells(
                clue.row,
                clue.col,
                clue.direction,
                clue.answer.length
              )
            }
          >
            {clue.clue}
          </div>
        ))}
        <h2>Down</h2>
        {crosswordData.clues.down.map((clue, index) => (
          <div
            key={index}
            className="clue"
            onClick={() =>
              highlightCells(
                clue.row,
                clue.col,
                clue.direction,
                clue.answer.length
              )
            }
          >
            {clue.clue}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Crossword;
