import React, { useState } from "react";
import Crossword from "@jaredreisinger/react-crossword";
import { useNavigate } from "react-router-dom";
import dramaria from "../../assets/image/kuis/dramaria.png";
import tragedi from "../../assets/image/kuis/tragedi.png";
import dagelan from "../../assets/image/kuis/dagelan.png";
import opera from "../../assets/image/kuis/opera.png";
import teater from "../../assets/image/kuis/teater.png";

const data = {
  across: {
    1: {
      clue: "1 Across",
      answer: "DRAMARIA",
      row: 1,
      col: 2,
    },
    3: {
      clue: "3 Across",
      answer: "OPERA",
      row: 4,
      col: 0,
    },
    5: {
      clue: "5 Across",
      answer: "TEATER",
      row: 6,
      col: 0,
    },
  },
  down: {
    2: {
      clue: "2 Down",
      answer: "TRAGEDI",
      row: 0,
      col: 7,
    },
    4: {
      clue: "4 Down",
      answer: "DAGELAN",
      row: 1,
      col: 2,
    },
  },
};

export default function UtakAtikGathukMurda() {
  const [isCorrect, setIsCorrect] = useState(false);
  const navigate = useNavigate();

  const handleCrosswordCorrect = () => {
    setIsCorrect(true);
  };

  const handleNextPage = () => {
    navigate("/aksaramurda/kuis3/nyocokakegambarmurda");
  };

  return (
    <div className="contain">
      <br />
      <h1 className="p-20 m-5 text-center">Utak Atik Gathuk Murda</h1>
      <br />
      <div className="crossword">
        <Crossword data={data} onCrosswordCorrect={handleCrosswordCorrect} />
      </div>
      <div className="clues text-center mt-4">
        <div>
          <img src={dramaria} alt="Dramaria" /> 1 Across
        </div>
        <div>
          <img src={tragedi} alt="Tragedi" /> 2 Down
        </div>
        <div>
          <img src={opera} alt="Opera" /> 3 Across
        </div>
        <div>
          <img src={dagelan} alt="Dagelan" /> 4 Down
        </div>
        <div>
          <img src={teater} alt="Teater" /> 5 Across
        </div>
      </div>
      {isCorrect && (
        <div className="text-center">
          <button onClick={handleNextPage} className="btn btn-primary mt-4">
            Next Page
          </button>
        </div>
      )}
    </div>
  );
}
