import React, { useState } from "react";
import Crossword from "@jaredreisinger/react-crossword";
import { useNavigate } from "react-router-dom";
import dramaria from "../../assets/image/kuis/dramaria.png";
import tragedi from "../../assets/image/kuis/tragedi.png";
import dagelan from "../../assets/image/kuis/dagelan.png";
import opera from "../../assets/image/kuis/opera.png";
import teater from "../../assets/image/kuis/teater.png";
import "../../style/Crossword.css"; // Assuming you have a CSS file for custom styles

const data = {
  across: {
    1: {
      clue: "Gambar 1", // Placeholder clue
      answer: "DRAMARIA",
      row: 1,
      col: 2,
    },
    3: {
      clue: "GAMBAR 3", // Placeholder clue
      answer: "OPERA",
      row: 4,
      col: 0,
    },
    5: {
      clue: "GAMBAR 5", // Placeholder clue
      answer: "TEATER",
      row: 6,
      col: 0,
    },
  },
  down: {
    2: {
      clue: "GAMBAR 2", // Placeholder clue
      answer: "TRAGEDI",
      row: 0,
      col: 7,
    },
    4: {
      clue: "GAMBAR 4", // Placeholder clue
      answer: "DAGELAN",
      row: 1,
      col: 2,
    },
  },
};

export default function UtakAtikGathukMurda() {
  const [isCorrect, setIsCorrect] = useState(false);
  const navigate = useNavigate();

  const handleCrosswordCorrect = () => {
    setIsCorrect(true);
  };

  const handleNextPage = () => {
    navigate("/aksaramurda/kuis3/nyocokakegambarmurda");
  };

  return (
    <div className="contain">
      <h1 className="p-20 m-5 text-center">Utak Atik Gathuk Murda</h1>
      <div className="game-container">
        <div className="crossword">
          <Crossword data={data} onCrosswordCorrect={handleCrosswordCorrect} />
        </div>
        <div className="clues-with-images">
          <div className="clue">
            <img src={dramaria} alt="Dramaria" className="clue-image" />{" "}
            <span>1 Across</span>
          </div>
          <div className="clue">
            <img src={tragedi} alt="Tragedi" className="clue-image" />{" "}
            <span>2 Down</span>
          </div>
          <div className="clue">
            <img src={opera} alt="Opera" className="clue-image" />{" "}
            <span>3 Across</span>
          </div>
          <div className="clue">
            <img src={dagelan} alt="Dagelan" className="clue-image" />{" "}
            <span>4 Down</span>
          </div>
          <div className="clue">
            <img src={teater} alt="Teater" className="clue-image" />{" "}
            <span>5 Across</span>
          </div>
        </div>
      </div>
      {isCorrect && (
        <div className="text-center">
          <button onClick={handleNextPage} className="btn btn-primary mt-4">
            Next Page
          </button>
        </div>
      )}
    </div>
  );
}
