import React, { useState } from "react";
import dramaria from "../../assets/image/kuis/dramaria.png";
import tragedi from "../../assets/image/kuis/tragedi.png";
import dagelan from "../../assets/image/kuis/dagelan.png";
import opera from "../../assets/image/kuis/opera.png";
import teater from "../../assets/image/kuis/teater.png";

// Define the crossword puzzle data with positions and clues
const crosswordData = {
  grid: [
    ["", "", "", "", "", "", "", "TRAGEDI"],
    ["D", "", "", "", "", "", "", ""],
    ["A", "", "", "", "", "", "", ""],
    ["G", "", "", "", "", "", "", ""],
    ["E", "", "", "", "", "", "", ""],
    ["L", "", "", "", "", "", "", ""],
    ["A", "", "", "", "", "", "", ""],
    ["N", "", "", "", "", "", "", ""],
  ],
  clues: {
    across: [
      {
        clue: <img src={dramaria} alt="Drama Clue" />,
        answer: "DRAMARIA",
        row: 1,
        col: 2,
        direction: "across",
      },
      {
        clue: <img src={opera} alt="Opera Clue" />,
        answer: "OPERA",
        row: 4,
        col: 0,
        direction: "across",
      },
      {
        clue: <img src={teater} alt="Teater Clue" />,
        answer: "TEATER",
        row: 6,
        col: 0,
        direction: "across",
      },
    ],
    down: [
      {
        clue: <img src={tragedi} alt="Tragedi Clue" />,
        answer: "TRAGEDI",
        row: 0,
        col: 7,
        direction: "down",
      },
      {
        clue: <img src={dagelan} alt="Dagelan Clue" />,
        answer: "DAGELAN",
        row: 1,
        col: 2,
        direction: "down",
      },
    ],
  },
};

const Crossword = ({ onCrosswordCorrect }) => {
  const [grid, setGrid] = useState(crosswordData.grid);
  const [highlightedCells, setHighlightedCells] = useState([]);

  const handleChange = (row, col, value) => {
    const newGrid = grid.map((r, i) =>
      r.map((cell, j) => (i === row && j === col ? value.toUpperCase() : cell))
    );
    setGrid(newGrid);
    checkCorrectness(newGrid);
  };

  const checkCorrectness = (newGrid) => {
    // Simple check: if all answers match
    const acrossAnswers = crosswordData.clues.across.map((clue) => clue.answer);
    const downAnswers = crosswordData.clues.down.map((clue) => clue.answer);

    const gridString = newGrid.flat().join("");
    const isCorrect =
      acrossAnswers.every((answer) => gridString.includes(answer)) &&
      downAnswers.every((answer) => gridString.includes(answer));

    if (isCorrect) {
      onCrosswordCorrect();
    }
  };

  const highlightCells = (row, col, direction, length) => {
    const cells = [];
    for (let i = 0; i < length; i++) {
      if (direction === "across") {
        cells.push({ row, col: col + i });
      } else {
        cells.push({ row: row + i, col });
      }
    }
    setHighlightedCells(cells);
  };

  return (
    <div className="crossword-grid">
      {grid.map((row, rowIndex) => (
        <div key={rowIndex} className="crossword-row">
          {row.map((cell, colIndex) => (
            <input
              key={colIndex}
              value={cell}
              onChange={(e) => handleChange(rowIndex, colIndex, e.target.value)}
              maxLength={1}
              className={`crossword-cell ${
                highlightedCells.some(
                  (c) => c.row === rowIndex && c.col === colIndex
                )
                  ? "highlighted"
                  : ""
              }`}
            />
          ))}
        </div>
      ))}
      <div className="clues">
        <h2>Across</h2>
        {crosswordData.clues.across.map((clue, index) => (
          <div
            key={index}
            className="clue"
            onClick={() =>
              highlightCells(
                clue.row,
                clue.col,
                clue.direction,
                clue.answer.length
              )
            }
          >
            {clue.clue}
          </div>
        ))}
        <h2>Down</h2>
        {crosswordData.clues.down.map((clue, index) => (
          <div
            key={index}
            className="clue"
            onClick={() =>
              highlightCells(
                clue.row,
                clue.col,
                clue.direction,
                clue.answer.length
              )
            }
          >
            {clue.clue}
          </div>
        ))}
      </div>
    </div>
  );
};

export default function UtakAtikGathukMurda() {
  const [isCorrect, setIsCorrect] = useState(false);
  const navigate = useNavigate();

  const handleCrosswordCorrect = () => {
    setIsCorrect(true);
  };

  const handleNextPage = () => {
    navigate("/aksaramurda/kuis3/nyocokakegambarmurda");
  };

  return (
    <div className="contain">
      <h1 className="p-20 m-5 text-center">Utak Atik Gathuk Murda</h1>
      <div className="game-container">
        <Crossword onCrosswordCorrect={handleCrosswordCorrect} />
      </div>
      {isCorrect && (
        <div className="text-center">
          <button onClick={handleNextPage} className="btn btn-primary mt-4">
            Next Page
          </button>
        </div>
      )}
    </div>
  );
}
